using System.Collections; using System.Collections.Generic; using UnityEngine; /* hehe one line of code */public class RotateCamera : MonoBehaviour{public float RotationSpeed = 1;          /* Variables */void Start(){/*Put Start code*/                     }void Update(){/*Put Update Code Here*/ float horizontalInput = Input.GetAxis("Horizontal");transform.Rotate(Vector3.up * RotationSpeed * -horizontalInput * Time.deltaTime);}}
