 /* you can use /* to comment on a line without making the entire thing a line, i learned this while learning vex PROS*/using System.Collections; using System.Collections.Generic; using UnityEngine; /* hehe one line of code */public class RotateCamera : MonoBehaviour{public float RotationSpeed = 1;          /* Variables */void Start(){/*Put Start code*/                     }void Update(){/*Put Update Code Here*/ float horizontalInput = Input.GetAxis("Horizontal");transform.Rotate(Vector3.up * RotationSpeed * horizontalInput * Time.deltaTime);}}
